def get_list_check(main_genre: str):
    questions = dict_for_third_question[main_genre][1]
    return questions


def get_question(main_genre: str):
    return dict_for_third_question[main_genre][0][0]


def keyboard_for_third_question(main_genre: str):
    kb_builder_function = ReplyKeyboardBuilder()
    questions = dict_for_third_question[main_genre][1]
    buttons_for_answer = [KeyboardButton(text=x) for x in questions]
    kb_builder_function.row(*buttons_for_answer, width=3)
    return kb_builder_function.as_markup(resize_keyboard=True)
